mapper.insert:
INSERT INTO bang_cham_cong(
    ma_nhan_vien
  , ngay
  , gio_vao
  , ma_loai_cong
)
VALUES (
    #{bangChamCong.maNhanVien}
  , #{bangChamCong.ngayChamCong}
  , #{bangChamCong.gioVao}
  , #{bangChamCong.maLoaiCong}
)

mapper.select:
SELECT
FROM bang_cham_cong bcc LEFT JOIN nhan_vien nv ON bcc.ma_nhan_vien = nv.ma_nhan_vien LEFT JOIN loai_cong lc ON bcc.ma_loai_cong = lc.ma_loai_cong
nv.ma_nhan_vien = #{maNhanVien}

select exists(
    select 1
    from bang_cham_cong
    where ma_nhan_vien = #{bangChamCong.maNhanVien}
      and ngay = #{bangChamCong.ngayChamCong}
)
