mapper.update:
UPDATE hoc_van
SET
    ten_trinh_do = #{tenTrinhDo},
    chuyen_nganh = #{chuyenNganh},
    nam_tot_nghiep = #{namTotNghiep},
    co_so_giao_duc = #{coSoGiaoDuc}
WHERE ma_nhan_vien = #{maNhanVien}
AND nam_tot_nghiep = #{namTotNghiep}
AND chuyen_nganh = #{chuyenNganh}

mapper.delete:
DELETE FROM hoc_van
WHERE ma_nhan_vien = #{maNhanVien}
AND nam_tot_nghiep = #{namTotNghiep}
AND chuyen_nganh = #{chuyenNganh}

mapper.select:
SELECT EXISTS(
    SELECT 1
    FROM hoc_van
    WHERE ma_nhan_vien = #{maNhanVien}
    AND nam_tot_nghiep = #{namTotNghiep}
    AND chuyen_nganh = #{chuyenNganh}
)
