mapper.select:
SELECT
FROM phe_duyet JOIN don_yeu_cau ON phe_duyet.ma_don = don_yeu_cau.ma_don JOIN nhan_vien nguoi_tao ON don_yeu_cau.ma_nhan_vien = nguoi_tao.ma_nhan_vien JOIN nhan_vien nguoi_phe_duyet ON phe_duyet.ma_nhan_vien = nguoi_phe_duyet.ma_nhan_vien
nguoi_phe_duyet.ma_nhan_vien = #{maNhanVien}

SELECT
FROM don_yeu_cau JOIN nhan_vien nguoi_tao ON don_yeu_cau.ma_nhan_vien = nguoi_tao.ma_nhan_vien JOIN phe_duyet ON don_yeu_cau.ma_don = phe_duyet.ma_don JOIN nhan_vien nguoi_phe_duyet ON phe_duyet.ma_nhan_vien = nguoi_phe_duyet.ma_nhan_vien
nguoi_phe_duyet.ma_nhan_vien = #{maNhanVien}
