mapper.select:
SELECT
FROM ung_vien join vi_tri_tuyen_dung on ung_vien.ma_vi_tri_tuyen_dung = vi_tri_tuyen_dung.ma_vi_tri_tuyen_dung
ten_vi_tri = #{tenViTri}

SELECT EXISTS (
    SELECT 1
    FROM ung_vien
    WHERE ma_ung_vien = #{maUngVien}
)

mapper.insert:
INSERT INTO ung_vien
    (ma_ung_vien
    , ho_ten
    , gioi_tinh
    , ngay_sinh
    , dien_thoai
    , cccd
    , dia_chi
    , hinh_anh
    , email
    , ma_vi_tri_tuyen_dung
) VALUES
      (generate_ma_ung_vien()
      , #{hoTen}
      , #{gioiTinh}
      , #{ngaySinh}::DATE
      , #{dienThoai}
      , #{cccd}
      , #{diaChi}
      , #{hinhAnh}
      , #{email}
      , #{maViTriTuyenDung});
