mapper.insert:
INSERT INTO ung_vien
    (ma_ung_vien
    , ho_ten
    , gioi_tinh
    , ngay_sinh
    , dien_thoai
    , cccd
    , dia_chi
    , hinh_anh
    , email
    , ma_vi_tri_tuyen_dung
    , trang_thai
) VALUES
      (generate_ma_ung_vien()
      , #{hoTen}
      , #{gioiTinh}
      , #{ngaySinh}::DATE
      , #{dienThoai}
      , #{cccd}
      , #{diaChi}
      , #{hinhAnh}
      , #{email}
      , #{maViTriTuyenDung}
      ,#{trangThai})

mapper.update:
UPDATE ung_vien SET ho_ten = #{hoTen}, gioi_tinh = #{gioiTinh}, ngay_sinh = #{ngaySinh}::DATE, dien_thoai = #{dienThoai}, cccd = #{cccd}, dia_chi = #{diaChi}, hinh_anh = #{hinhAnh}, email = #{email}, ma_vi_tri_tuyen_dung = #{maViTriTuyenDung}, ngay_phong_van = #{ngayPhongVan}, dia_diem_phong_van = #{diaDiemPhongVan}, ngay_bat_dau_lam_viec = #{ngayBatDatLam},
,trang_thai = #{trangThai}
WHERE ma_ung_vien = #{maUngVien}
