mapper.insert:
    insert into vi_pham(
        ma_vi_pham,
        ten_vi_pham,
        so_tien
) VALUES (
    generate_ma_vi_pham(),
    #{tenViPham},
    #{soTienViPham}
)

mapper.update:
update vi_pham
set ten_vi_pham = #{tenViPham},
        so_tien = #{soTienViPham}
where ma_vi_pham = #{maViPham}

mapper.select:
SELECT EXISTS(
    SELECT 1
    FROM vi_pham
    WHERE ma_vi_pham = #{maViPham}
)
